INSERT INTO `products` (`pro_id`, `pro_name`, `cate_id`, `price`, `intro`, `detail`, `pro_image`, `quantity`) VALUES
(3, 'Iphone 10 64GB', 1, 999, 'Tiếp tục bị Trung Quốc kiện về bằng sáng chế Siri, Apple có nguy cơ mất trắng 1,4 tỷ USD', 'Vụ tranh chấp xoay quanh việc Apple sử dụng trái phép các bằng sáng chế của Trung Quốc trong Siri lại tiếp tục với những diễn biến mới. Lần này, phía Trung Quốc yêu cầu Apple phải đền bù thiệt hại lên đến 1,43 tỷ USD, và trong trường hợp xấu nhất, gã khổng lồ công nghệ Mỹ nhiều khả năng bị cấm tại thị trường lớn nhất thế giới này.\r\n\r\nTám năm đã trôi qua kể từ lần đầu công ty Trung Quốc là Shanghai Zhizhen Network Technology kiện Apple, cả hai lại một lần nữa phải gặp nhau tại toà. Công ty này một mực khẳng định Apple đã vi phạm các bằng sáng chế của họ, và kêu gọi toà án đưa ra phán quyết nhằm ngăn không cho Apple bán các thiết bị của hãng tại Trung Quốc.', 'apple.png', 100),
(4, 'Điện thoại Samsung Galaxy A51 (6GB/128GB)', 2, 455, 'Theo Strategy Analytics, Galaxy A51 là Smartphone Android Bán Chạy Nhất Thế Giới Quý 1/2020, máy sở hữu cụm 4 camera, bao gồm camera macro chụp cận cảnh lần đầu xuất hiện trên smartphone Samsung, màn ', 'Thiết kế thời thượng, bật cá tính\r\nMáy có thiết kế mỏng nhẹ thuộc hàng top trong phân khúc, chỉ 7.9 mm. Mặt lưng với họa tiết cắt kim cương đa sắc nổi bật, đi kèm là 3 tùy chọn màu sắc sành điệu: Xanh Crush Đa Sắc, Trắng Crush Lấp Lánh, Đen Crush Kim Cương.\r\nMàn hình của điện thoại Galaxy A51 hứa hẹn mang đến trải nghiệm hình ảnh tuyệt vời nhờ màn hình tràn viền vô cực Infinity-O 6.5 inch. Độ phân giải FullHD+ tái hiện hình ảnh rõ ràng sắc nét. Bên cạnh đó tấm nền Super AMOLED cho màu đen sâu, có tùy chọn màu sắc tươi sáng hoặc tiêu chuẩn theo sở thích người dùng.', 'samsung-galaxy-a31-400x460-400x460.png', 100),
(5, 'Điện thoại iPhone 11 Pro Max 64GB', 1, 1199, 'Trong năm 2019 thì chiếc smartphone được nhiều người mong muốn sở hữu trên tay và sử dụng nhất không ai khác chính là iPhone 11 Pro Max 64GB tới từ nhà Apple.', '<h2 dir=\"ltr\" style=\"margin: 0px 0px 10px; padding: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 1.3em; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; color: #333333; outline: none; zoom: 1; overflow: hidden;\">Trong năm 2019 th&igrave; chiếc&nbsp;<a style=\"margin: 0px; padding: 0px; text-decoration-line: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; line-height: 18px; color: #50a8e3; outline: none; zoom: 1;\" title=\"Tham khảo gi&aacute; điện thoại smartphone ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a>&nbsp;được nhiều người mong muốn sở hữu tr&ecirc;n tay v&agrave; sử dụng nhất kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave;&nbsp;<a style=\"margin: 0px; padding: 0px; text-decoration-line: none; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; line-height: 18px; color: #50a8e3; outline: none; zoom: 1;\" title=\"Tham khảo điện thoại iPhone 11 Pro Max 64GB ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd/iphone-11-pro-max\" target=\"_blank\" rel=\"noopener\">iPhone 11 Pro Max 64GB</a>&nbsp;tới từ nh&agrave; Apple.</h2>\r\n<h3 dir=\"ltr\" style=\"margin: 30px 0px 0px; padding: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 20px; line-height: 1.3em; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; color: #333333; outline: none; zoom: 1; overflow: hidden;\">Camera được cải tiến mạnh mẽ</h3>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px;\">Chắc chắn l&yacute; do lớn nhất m&agrave; bạn muốn n&acirc;ng cấp l&ecirc;n iPhone 11 Pro Max ch&iacute;nh l&agrave; cụm camera mới được Apple n&acirc;ng cấp rất nhiều.</p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><a class=\"preventdefault\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" href=\"https://www.thegioididong.com/images/42/200533/iphone-11-pro-max-tgdd14-1.jpg\"><img class=\"lazy\" style=\"margin: 10px auto; padding: 0px; border: 0px; display: block; max-width: 100%; height: auto; max-height: 584px;\" title=\"iPhone 11 Pro Max 64GB | Cụm ba camera sau ấn tượng\" src=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd14-1.jpg\" alt=\"iPhone 11 Pro Max 64GB | Cụm ba camera sau ấn tượng\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd14-1.jpg\" /></a></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px;\">Lần đầu ti&ecirc;n ch&uacute;ng ta sẽ c&oacute; một chiếc&nbsp;<a style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" title=\"Tham khảo iPhone ch&iacute;nh h&atilde;ng\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">iPhone</a>&nbsp;với 3 camera ở mặt sau v&agrave; cả 3 camera n&agrave;y đều c&oacute; độ ph&acirc;n giải l&agrave; 12 MP.</p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><a class=\"preventdefault\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" href=\"https://www.thegioididong.com/images/42/200533/iphone-11-pro-max-tgdd6-1.jpg\"><img class=\"lazy\" style=\"margin: 10px auto; padding: 0px; border: 0px; display: block; max-width: 100%; height: auto; max-height: 584px;\" title=\"Điện thoại iPhone 11 Pro Max 64GB | Giao diện chụp ảnh camera\" src=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd6-1.jpg\" alt=\"Điện thoại iPhone 11 Pro Max 64GB | Giao diện chụp ảnh camera\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd6-1.jpg\" /></a></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px;\">Ch&uacute;ng ta sẽ c&oacute; một camera g&oacute;c thường, một camera g&oacute;c rộng v&agrave; một camera tele đ&aacute;p ứng đầy đủ nhu cầu chụp ảnh h&agrave;ng ng&agrave;y của người d&ugrave;ng.</p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><a class=\"preventdefault\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" href=\"https://www.thegioididong.com/images/42/200533/iphone-11-pro-max-tgdd9-1.jpg\"><img class=\"lazy\" style=\"margin: 10px auto; padding: 0px; border: 0px; display: block; max-width: 100%; height: auto; max-height: 584px;\" title=\"Điện thoại iPhone 11 Pro Max 64GB | Ảnh chụp ch&acirc;n dung bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd9-1.jpg\" alt=\"Điện thoại iPhone 11 Pro Max 64GB | Ảnh chụp ch&acirc;n dung bằng camera sau\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd9-1.jpg\" /></a></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px; display: block; overflow: hidden; font-size: 13px; color: #666666;\">Ảnh chụp bằng camera sau tr&ecirc;n iPhone 11 Pro Max</em></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px;\">Một cải tiến nữa cũng rất đ&aacute;ng ch&uacute; &yacute; ch&iacute;nh l&agrave;&nbsp;<a style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" title=\"T&igrave;m hiểu chế độ Night Mode\" href=\"https://www.thegioididong.com/hoi-dap/night-mode-la-gi-diem-khac-nhau-giu-night-mode-va-1197122\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu chế độ Night Mode\">chế độ Night Mode</a>&nbsp;mới sẽ gi&uacute;p bạn cải thiện rất nhiều chất lượng ảnh chụp đ&ecirc;m tr&ecirc;n iPhone.</p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><a class=\"preventdefault\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" href=\"https://www.thegioididong.com/images/42/200533/iphone-11-pro-max-tgdd15-1.jpg\"><img class=\"lazy\" style=\"margin: 10px auto; padding: 0px; border: 0px; display: block; max-width: 100%; height: auto; max-height: 584px;\" title=\"Điện thoại iPhone 11 Pro Max 64GB | Ảnh chụp bằng camera sau\" src=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd15-1.jpg\" alt=\"Điện thoại iPhone 11 Pro Max 64GB | Ảnh chụp bằng camera sau\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd15-1.jpg\" /></a></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px; display: block; overflow: hidden; font-size: 13px; color: #666666;\">Ảnh chụp bằng camera sau tr&ecirc;n iPhone 11 Pro Max</em></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px;\">T&ugrave;y thuộc v&agrave;o điều kiện m&ocirc;i trường bạn chụp m&agrave; iPhone sẽ đưa ra những th&ocirc;ng số ph&ugrave; hợp để bạn c&oacute; thể c&oacute; cho m&igrave;nh được một bức ảnh ưng &yacute; nhất.</p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><a class=\"preventdefault\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" href=\"https://www.thegioididong.com/images/42/200533/iphone-11-pro-max-tgdd12.jpg\"><img class=\"lazy\" style=\"margin: 10px auto; padding: 0px; border: 0px; display: block; max-width: 100%; height: auto; max-height: 584px;\" title=\"Điện thoại iPhone 11 Pro Max 64GB | Chế độ Night Mode mới\" src=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd12.jpg\" alt=\"Điện thoại iPhone 11 Pro Max 64GB | Chế độ Night Mode mới\" data-original=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-tgdd12.jpg\" /></a></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px; display: block; overflow: hidden; font-size: 13px; color: #666666;\">Chế độ Night Mode mới</em></p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px;\">Chế độ ch&acirc;n dung kh&ocirc;ng chỉ tốt hơn trong việc lấy n&eacute;t v&agrave;o đối tượng muốn chụp, m&agrave; c&ograve;n hoạt động được ở khoảng c&aacute;ch b&igrave;nh thường nhờ sự trợ gi&uacute;p của cảm biến độ s&acirc;u.</p>\r\n<p dir=\"ltr\" style=\"margin: 10px 0px; padding: 0px; margin-block-start: 0px; margin-block-end: 0px; text-rendering: geometricprecision; color: #333333; font-family: Helvetica, Arial, DejaVu Sans, Liberation Sans, Freesans, sans-serif; font-size: 16px; text-align: center;\"><a class=\"preventdefault\" style=\"margin: 0px; padding: 0px; text-decoration-line: none; color: #50a8e3;\" href=\"https://www.thegioididong.com/images/42/200533/iphone-11-pro-max-tgdd7-1.jpg\"><img class=\"lazy\" style=\"margin: 10px auto; padding: 0px; border: 0px; display: block; max-width: 100%; height: auto; max-height: 584px;\" title=\"Điện thoại iPhone 11 Pro Max 64GB | Ảnh chụp bằng chế độ Night Mode\" src=\"https://cdn